// Add <RecipeID> to a craft table
OnModLoad(function()
{
    // Properties
    var _craftTableID = 0;
    var _recipeID = [%$%mining_crossbow@Mining_crossbow_recipe%$%];

    // Add recipe to craft table
    var _dictionary = variable_global_get(%$%__DICTIONARY_DB_CRAFT_TABLE__%$%);
    if(is_string(_craftTableID))
    {
        _craftTableID = _dictionary[? _craftTableID];
    }
    var _global = variable_global_get(%$%__DB_CRAFT_TABLE%$%);
    // Loop through each index of array %$%_recipeID%$% and add the recipe to the above crafting table
    for (var i = 0; i < array_length(_recipeID); i++) {
    	array_push(_global[_craftTableID][E_CRAFT_TABLE_DATA.item_list]#$#[_recipeID[i]]);
    }
});
